// src/components/ChatBot.js
"use client";

import { useState, useRef, useEffect } from "react";
import { Moon, Sun, Send, Upload } from "lucide-react";
import { marked } from "marked";
import { BASE_API_URL } from "../api";

const Chatbot = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const [darkMode, setDarkMode] = useState(false);
  const [isTyping, setIsTyping] = useState(false);
  const messagesEndRef = useRef(null);

  const suggestions = [
    "What is AI?",
    "How does a car engine work?",
    "What are the benefits of exercise?",
    "Explain quantum computing.",
    "What is machine learning?",
  ];

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  const handleSend = async () => {
    if (!input.trim()) return;

    console.log("User input:", input);

    const queries = input
      .split(/[?;]/)
      .map((q) => q.trim())
      .filter((q) => q);
    let botResponses = [];

    for (let query of queries) {
      if (!query) continue;

      const newMessage = {
        id: Date.now() + Math.random(),
        text: query,
        sender: "user",
      };
      setMessages((prev) => [...prev, newMessage]);
      setInput("");
      setIsTyping(true);

      if (query.length < 5) {
        const enrichmentMessage = {
          id: Date.now() + Math.random(),
          text: "I'm enhancing your query for a better answer...",
          sender: "bot",
        };
        setMessages((prev) => [...prev, enrichmentMessage]);
      }

      try {
        const response = await fetch(`${BASE_API_URL}/query`, {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({ user_input: query }),
        });

        if (!response.ok)
          throw new Error("Failed to fetch response from server");

        const data = await response.json();
        const formattedResponse = marked(data.response);

        const botResponse = {
          id: Date.now() + Math.random(),
          text: formattedResponse,
          sender: "bot",
          sources: data.sources || "No source available",
        };
        botResponses.push(botResponse);
      } catch (error) {
        console.error("Error fetching response:", error);
        const errorMessage = {
          id: Date.now() + Math.random(),
          text: "Error: Unable to connect to the server. Please try again later.",
          sender: "bot",
        };
        botResponses.push(errorMessage);
      } finally {
        setIsTyping(false);
      }
    }

    setMessages((prev) => [...prev, ...botResponses]);
  };

  const handleInputChange = (e) => {
    setInput(e.target.value);
  };

  const handleFileUpload = async (e) => {
    const file = e.target.files[0];
    if (file) {
      console.log("File selected:", file.name);

      const formData = new FormData();
      formData.append("pdf", file);

      try {
        const response = await fetch(`${BASE_API_URL}/upload`, {
          method: "POST",
          body: formData,
        });

        if (!response.ok) throw new Error("Failed to upload file");

        console.log("File uploaded successfully");
        setMessages((prev) => [
          ...prev,
          {
            id: Date.now() + Math.random(),
            text: `Successfully uploaded: ${file.name}`,
            sender: "bot",
          },
        ]);
      } catch (error) {
        console.error("Error uploading file:", error);
        setMessages((prev) => [
          ...prev,
          {
            id: Date.now() + Math.random(),
            text: `Error uploading file: ${file.name}`,
            sender: "bot",
          },
        ]);
      }
    }
  };

  return (
    <div
      className={`h-screen flex flex-col ${
        darkMode
          ? "bg-gray-900 text-white"
          : "bg-gradient-to-br from-purple-50 to-indigo-100 text-gray-900"
      } transition-colors duration-300`}
    >
      <header className="flex justify-between items-center p-4 bg-white dark:bg-gray-800 shadow-md rounded-b-lg">
        <h1 className="text-3xl font-bold font-mono text-purple-600 dark:text-purple-400">
          RAG Bot
        </h1>
        <div className="flex items-center space-x-4">
          <button
            onClick={() => setDarkMode(!darkMode)}
            className="rounded-full p-2 hover:bg-purple-100 dark:hover:bg-gray-700"
          >
            {darkMode ? (
              <Sun className="h-6 w-6 text-yellow-400" />
            ) : (
              <Moon className="h-6 w-6 text-purple-600" />
            )}
          </button>
          <label className="cursor-pointer">
            <Upload className="h-6 w-6 text-purple-600 dark:text-purple-400" />
            <input
              type="file"
              accept=".pdf"
              className="hidden"
              onChange={handleFileUpload}
            />
          </label>
        </div>
      </header>

      <main className="flex-grow overflow-auto p-4">
        <div className="max-w-3xl mx-auto space-y-4">
          {messages.map((message) => (
            <div
              key={message.id}
              className={`flex ${
                message.sender === "user" ? "justify-end" : "justify-start"
              }`}
            >
              <div
                className={`max-w-sm md:max-w-lg lg:max-w-xl p-4 rounded-xl ${
                  message.sender === "user"
                    ? "bg-purple-500 text-white rounded-br-none"
                    : "bg-white text-gray-900 dark:bg-gray-800 dark:text-white rounded-bl-none"
                } shadow-lg transition-transform duration-300 hover:scale-105`}
              >
                {message.sender === "bot" ? (
                  <div
                    className="markdown-content"
                    dangerouslySetInnerHTML={{ __html: message.text }}
                  ></div>
                ) : (
                  <p>{message.text}</p>
                )}
                {message.sender === "bot" &&
                  message.sources !== "No source available" && (
                    <div className="text-sm text-gray-500 mt-2">
                      <strong>Sources:</strong>
                      {Array.isArray(message.sources)
                        ? message.sources.map((source, index) => (
                            <div key={index}>
                              <a
                                href={source}
                                target="_blank"
                                rel="noopener noreferrer"
                                className="text-blue-500 underline"
                              >
                                {source}
                              </a>
                            </div>
                          ))
                        : message.sources}
                    </div>
                  )}
              </div>
            </div>
          ))}
          {isTyping && (
            <div className="flex justify-start">
              <div className="bg-white dark:bg-gray-800 p-3 rounded-lg rounded-bl-none shadow-lg">
                <div className="flex space-x-2">
                  <div className="w-3 h-3 bg-purple-400 rounded-full animate-bounce"></div>
                  <div
                    className="w-3 h-3 bg-purple-400 rounded-full animate-bounce"
                    style={{ animationDelay: "0.2s" }}
                  ></div>
                  <div
                    className="w-3 h-3 bg-purple-400 rounded-full animate-bounce"
                    style={{ animationDelay: "0.4s" }}
                  ></div>
                </div>
              </div>
            </div>
          )}
          <div ref={messagesEndRef} />
        </div>
      </main>

      <footer className="p-4 bg-white dark:bg-gray-800 shadow-md rounded-t-lg">
        <form
          onSubmit={(e) => {
            e.preventDefault();
            handleSend();
          }}
          className="flex items-center space-x-2 max-w-3xl mx-auto"
        >
          <input
            type="text"
            placeholder="Type your message..."
            value={input}
            onChange={handleInputChange}
            className="flex-grow bg-gray-100 dark:bg-gray-700 border-purple-300 dark:border-gray-600 focus:ring-purple-500 focus:border-purple-500 p-3 rounded-lg"
          />
          <button
            type="submit"
            className="rounded-full bg-purple-500 hover:bg-purple-600 text-white p-3"
          >
            <Send className="h-4 w-4" />
            <span className="sr-only">Send</span>
          </button>
        </form>

        {input && (
          <div className="bg-white dark:bg-gray-800 p-2 mt-2 rounded-lg shadow-md max-w-3xl mx-auto">
            {suggestions
              .filter((suggestion) =>
                suggestion.toLowerCase().includes(input.toLowerCase())
              )
              .map((suggestion, index) => (
                <div
                  key={index}
                  onClick={() => setInput(suggestion)}
                  className="p-2 cursor-pointer hover:bg-purple-100 dark:hover:bg-gray-700"
                >
                  {suggestion}
                </div>
              ))}
          </div>
        )}
      </footer>
    </div>
  );
};

export default Chatbot;
